import { useState, useEffect } from "react";
import ExerciseDropdownSearch from "../components/ExerciseDropdownSearch";
import ExerciseList from "../components/ExerciseList";
import Exercise from "../components/Exercise";

const NewWorkoutContainer = () => {

    const[allExercises, setAllExercises] = useState([]);
    const [dropSelect, setDropSelect] = useState(null);
    const [selectedExercise, setSelectedExercise] = useState(null);

    // const handleNewExercise = (event) => {
    //     event.preventDefault();

    //     //when submit clicked, get exercise according to id
    //     getExerciseTargetById();

    //     //get exercise to display
    //     <p>display exercise</p>
    //     // <Exercise exercise = {selectedExercise}/>

    //     setSelectedExercise(null);
    //     //add exercise to list and display


    // }

    const getExerciseTargetById = () => {
        if(dropSelect) {
            fetch(`http://localhost:8080/api/v1/data/${dropSelect}`)
            .then(response => response.json())
            .then(data => setSelectedExercise(data));
        }
    }

    useEffect(handleNewExercise, []);

    const updateDrop=(event) => {
        setDropSelect(event.target.value);
    }

    const getAllExercises = () => {
        fetch("http://localhost:8080/api/v1/exercises")
            .then(response => response.json())
            .then(data => setAllExercises(data))
    }

    useEffect(getAllExercises, []);

    // const getExerciseFromSelect
    return (
        allExercises.length > 0
        ?
        <>
        <p>NavBar</p> 
        <p>New Workout Page</p>
        <form>
            <ExerciseDropdownSearch allExercises = {allExercises} dropSelect={dropSelect} updateDrop={updateDrop}/>
            <input type="submit" value="Add" onSubmit={getExerciseTargetById}/>
        </form>
        {/* <ExerciseList allExercises={allExercises}/> */}
        </>
        :
        <p>Loading...</p>
    )
}
export default NewWorkoutContainer;
